//variables needed : wins, losses, randomcomputernumber, random crystal value(x4), userscore,
//The game will start with the computer generating a random number from 1-100
//the random number chosen will then be displayed to the user
//4 images of crystals will be generated
//each of the 4 crystals will have a random number from 1-10 as a value
//the users score will start at 0
//when a crystal is clicked, its value will be added to the score, allowing the user to figure out what value it has
//the user must then get their score equal to the random number generated by the computer
//if the users score matches the random number, wins go up one and the proccess is ran again
//if the user goes over the random number, losses goes up by one and the proccess is ran again.
$(document).ready(function() {
	var wins = 0;
	var winsText = $("#wins");
	var losses = 0;
	var lossesText = $("#losses");
	var userScore = 0;
	var computerNumber = Math.floor((Math.random() * 100) + 1);
	$("#computer-number").text("Number to Guess: "+ computerNumber);
	$("#wins").text("Wins: " + wins);
	$("#losses").text("Losses: " + losses);

		function resetGame2() {
		var computerNumber = Math.floor((Math.random() * 100) + 1);
		userScore = 0

		$("#score").text("Your score is: " + user);
		$("#computer-number").text("Number to Guess: "+ computerNumber);
		$("#wins").text("Wins: " + wins);
		$("#losses").text("Losses: " + losses);
		$("#crystalImg").html(" ");
		startGame();


	}
	
	function resetGame() {
		var computerNumber = Math.floor((Math.random() * 100) + 1);
		userScore = 0

		$("#score").text("Your score is: " + 0);
		$("#computer-number").text("Number to Guess: "+ computerNumber);
		$("#wins").text("Wins: " + 0);
		$("#losses").text("Losses: " + 0);
		$("#crystalImg").html(" ");
		startGame();


	}

function startGame() {



	for (var i = 0; i < 4; i++) {

		var crystalNumber = Math.floor(Math.random() * 10 + 1);

		var imgCrystal = $("<img>");

		imgCrystal.addClass("crystal-img");

		imgCrystal.attr("src", "assets/images/Crystal.png");

		imgCrystal.attr("data-crystalvalue", crystalNumber);
		imgCrystal.css("width: 250px");

		$("#crystalImg").append(imgCrystal);



	}

	$(".crystal-img").on("click", function() {

		var crystalValue = ($(this).attr("data-crystalvalue"));
		crystalValue = parseInt(crystalValue);

		userScore += crystalValue;

		$("#score").text("Your Score is: " + userScore);
		
		if (userScore === computerNumber) {
			wins++;


			
		}
		else if (userScore >= computerNumber) {
			resetGame();
			losses++;
			


		}
		$("#wins").text("Wins: " + wins);
		$("#losses").text("Losses: " + losses);

	});
}

startGame();
$("#resetButton").on("click", function() {
	resetGame();
})



});



